 p_id=>wwv_flow_api.id(20080455515420937968)
,p_plugin_type=>'REGION TYPE'
,p_name=>'COM.APEXCONSULTING.APEX.JET.TTWOSERIES'
,p_display_name=>'Time Line 2 Series'
,p_supported_ui_types=>'DESKTOP:JQM_SMARTPHONE'
,p_image_prefix => nvl(wwv_flow_application_install.get_static_plugin_file_prefix('REGION TYPE','COM.APEXCONSULTING.APEX.JET.TTWOSERIES'),'')
,p_javascript_file_urls=>wwv_flow_string.join(wwv_flow_t_varchar2(
'[require jet]#PLUGIN_FILES#timelinetwoserie.js',
''))
,p_css_file_urls=>'/i/libraries/oraclejet/4.2.0/css/libs/oj/v4.2.0/alta/oj-alta-notag-min.css'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'/*',
' * render - function to create placeholder div tag, and initialise the  component',
' */',
' function render ',
'( p_region                in  apex_plugin.t_region',
', p_plugin                in  apex_plugin.t_plugin',
', p_is_printer_friendly   in  boolean ',
') return apex_plugin.t_region_render_result ',
'is',
'  c_region_static_id      constant varchar2(255)  := apex_escape.html_attribute( p_region.static_id );',
'begin',
'  -- Add placeholder div',
'  sys.htp.p (',
'     ''<div class="a-JET-timeline" id="'' || c_region_static_id || ''_region">'' ||',
'       ''<div class="a-JET-timeline-container" id="'' || c_region_static_id || ''_chart"></div>'' ||',
'     ''</div>'' );',
'     ',
'  -- Initialize the chart',
'  apex_javascript.add_onload_code',
'  ( p_code => ''jet.timelinetwoserie.init(''||',
'                  ''"#''||c_region_static_id||''_chart", ''          || -- pRegionId',
'                  ''"'' || apex_plugin.get_ajax_identifier ||''"''   || -- pApexAjaxIdentifier',
'                 '')''',
'  );',
'  ',
'  return null;',
'end render;',
'',
'/*',
' * ajax - function to process SQL query, and output JSON data for legend',
' */',
'function ajax (',
'    p_region    in  apex_plugin.t_region,',
'    p_plugin    in  apex_plugin.t_plugin ',
') return apex_plugin.t_region_ajax_result',
'is',
'  l_column_value_list  apex_plugin_util.t_column_value_list;',
'begin  ',
'',
'  -- Process SQL query',
'  l_column_value_list := apex_plugin_util.get_data (',
'                             p_sql_statement      => p_region.source,',
'                             p_min_columns        => 2,',
'                             p_max_columns        => 5,',
'                             p_component_name     => p_region.name );',
'  ',
'   apex_json.open_object;',
'   ',
'    -------',
'     apex_json.open_object(''overview'');',
'     apex_json.write(''rendered'', ''on'');',
'     apex_json.close_object;',
'  ------',
'  ',
'    apex_json.open_object(''style'');',
'    apex_json.write(''width'', ''100%'');',
'    apex_json.write(''height'', ''950px'');',
'    apex_json.close_object;',
'    ',
'    apex_json.open_object(''majorAxis'');',
'    apex_json.write(''rendered'', ''on'');',
'    apex_json.open_object(''svgStyle'');',
'    apex_json.write(''height'', ''450px'');',
'    apex_json.close_object;',
'    apex_json.close_object;',
'    ',
'     ',
'   ',
'  ',
'   ',
'   apex_json.open_object(''minorAxis'');',
'   apex_json.write(''scale'', ''weeks'');',
'   apex_json.open_array(''zoomOrder''); -- "array": [',
'  apex_json.write(''months''); -- 1',
'   apex_json.write(''weeks''); -- 1',
'    apex_json.write(''days''); -- 1',
'  apex_json.close_array; -- ]',
'   apex_json.close_object;',
'   apex_json.write(''minorAxis.scale'', ''quarters.'');',
'    apex_json.write(''start'', ''01-JAN-13'');',
'    apex_json.write(''end'', ''31-DEC-13'');',
'    apex_json.write(''viewportStart'', ''01-MAY-13'');',
'    apex_json.write(''viewportEnd'', ''15-JUL-13'');',
'    apex_json.write(''selectionMode'', ''single'');',
'     apex_json.write(''selection'', ''e4'');',
'  apex_json.open_array(''series'');',
'    apex_json.open_object;',
'  apex_json.write(''id'', ''s1'');',
'  apex_json.write(''emptyText'', ''No Data.'');',
'  apex_json.write(''label'', ''Oracle Events'');',
'  apex_json.open_array(''items'');',
'  for i in 1 .. l_column_value_list(1).count loop',
'      apex_json.open_object;',
'       apex_json.write( ''id'', l_column_value_list(2)(i) );',
'      apex_json.write( ''title'',  l_column_value_list(1)(i) );',
'       apex_json.write( ''start'', l_column_value_list(4)(i) );',
'       apex_json.write( ''end'', l_column_value_list(5)(i) );',
'      apex_json.write( ''description'', l_column_value_list(3)(i) );',
'     ',
'      ',
'      apex_json.close_object;',
'  end loop;',
'  ',
' ',
'    apex_json.close_array;',
'      apex_json.close_object;',
'    ',
'     -----------------',
'  apex_json.open_object;',
'  apex_json.write(''id'', ''s1'');',
'  apex_json.write(''emptyText'', ''No Data.'');',
'  apex_json.write(''label'', ''Oracle Events'');',
'    ',
'     APEX_JSON.open_array(''items''); -- employees: [',
'  ',
'  FOR cur_rec IN (SELECT',
'    "id",',
'    "title",',
'    "start1",',
'    "end",',
'    "description"',
'  FROM',
'    seriestwo)',
'  LOOP',
'    APEX_JSON.open_object; -- {',
'     apex_json.write( ''id'', cur_rec."id" );',
'      apex_json.write( ''title'',  cur_rec."title" );',
'       apex_json.write( ''start'', cur_rec."start1" );',
'        apex_json.write( ''end'', cur_rec."end" );',
'      apex_json.write( ''description'', cur_rec."description" );',
'    APEX_JSON.close_object; -- } employee',
'  END LOOP;',
'',
'  APEX_JSON.close_array; -- ] employees',
'      apex_json.close_object;',
'  ',
'     ------------------',
'   apex_json.close_array;',
'  apex_json.close_object;',
'  ',
'  return null;',
'end ajax;'))
,p_api_version=>1
,p_render_function=>'render'
,p_ajax_function=>'ajax'
,p_standard_attributes=>'SOURCE_SQL'
,p_substitute_attributes=>false
,p_subscribe_plugin_settings=>true
,p_version_identifier=>'1.0.0'
,p_files_version=>94
);
